package com.iyam.catatanku.adsimport android.app.Activityimport android.widget.LinearLayoutimport com.google.android.gms.ads.AdSizeimport com.google.android.gms.ads.AdViewimport com.google.android.gms.ads.MobileAdsimport com.google.android.gms.ads.AdRequestinterface AdsManager {    fun initAd()    fun showBannerAd(container: LinearLayout)    companion object {        const val AD_STATUS = true        const val AD_TYPE = "AdMob"        const val ADMOB_TYPE = "AdMob"        const val ADMOB_BANNER_ID = "ca-app-pub-3940256099942544/9214589741"    }}class AdsManagerImpl(private var activity: Activity) : AdsManager {    override fun initAd() {        if (AdsManager.AD_STATUS) {            if (AdsManager.AD_TYPE.equals(AdsManager.ADMOB_TYPE, ignoreCase = true)) {                MobileAds.initialize(activity)            }        }    }    override fun showBannerAd(container: LinearLayout) {        if (AdsManager.AD_STATUS) {            if (AdsManager.AD_TYPE.equals(AdsManager.ADMOB_TYPE, ignoreCase = true)) {                val adView = AdView(activity)                adView.setAdSize(AdSize.BANNER)                adView.adUnitId = AdsManager.ADMOB_BANNER_ID                container.addView(adView)                val adRequest = AdRequest.Builder().build()                adView.loadAd(adRequest)            }        }    }}